import * as React from "react";
import { MouseEvent } from "react";
import { ColorType, IconType } from "../../typings";
export type ButtonVariant = "primary" | "secondary" | "tertiary" | "clear" | "alternative" | "gradient" | "IAGradient";
export interface ButtonProps {
    accessibilityLabel?: string;
    children?: any;
    color?: ColorType;
    dataTest?: string;
    disabled?: boolean;
    expand?: boolean;
    iconLeft?: IconType;
    iconRight?: IconType;
    inline?: boolean;
    onClick?: (e: MouseEvent<HTMLElement>) => void;
    size?: "small" | "medium";
    type?: "submit" | "button";
    uppercase?: boolean;
    className?: string;
    variant?: ButtonVariant;
}
declare const Button: {
    (Props: ButtonProps): React.JSX.Element;
    defaultProps: {
        variant: string;
        disabled: boolean;
        size: string;
        type: string;
        inline: boolean;
        accessibilityLabel: string;
        uppercase: boolean;
        expand: boolean;
    };
};
export default Button;
