import * as React from "react";
import { MouseEvent } from "react";
import { ColorType } from "../../typings";
export interface CardProps {
    children?: any;
    width?: number;
    expand?: boolean;
    completed?: boolean;
    backgroundColor?: ColorType;
    borderColor?: ColorType;
    onClick?: (e: MouseEvent<HTMLElement>) => void;
    size?: "medium" | "small";
}
declare const Card: {
    (Props: CardProps): React.JSX.Element;
    defaultProps: {
        width: number;
        expand: boolean;
        completed: boolean;
        size: string;
    };
};
export default Card;
