import { getFormula } from '.'
import { DataBuilderFormulaLookup } from './lookup'

describe('data-builder/formulas/lookup', () => {
  it('should parse $lookup formulas', () => {
    expect(
      getFormula({
        $lookup: {
          query: { key: 'value' },
          createIfNotFound: true,
        },
      }),
    ).toEqual(
      new DataBuilderFormulaLookup({
        query: { key: 'value' },
        createIfNotFound: true,
      }),
    )
  })
})
