import { DataBuilderFormulaType, getSchemaFromValue } from '.'
import { DataBuilderFormulaBase } from './base'

export class DataBuilderFormulaPlain extends DataBuilderFormulaBase {
  constructor(public value) {
    super(DataBuilderFormulaType.PLAIN, value)
  }

  getValue() {
    return this.value
  }

  getSchema() {
    return getSchemaFromValue(this.value, null, true)
  }
}
