import { DataRecord } from '../../data-collections'
import { DataBuilderFormulaType } from '.'
import { DataBuilderFormulaBase } from './base'

export class DataBuilderFormulaRecord extends DataBuilderFormulaBase {
  constructor(public record: DataRecord) {
    super(DataBuilderFormulaType.RECORD, record)
  }

  getValue() {
    return this.record?.id
  }

  getSchema() {
    return {
      type: 'string',
    }
  }
}
