/**
 * Parses string into a date object, taking timezones in account.
 * @param str
 * @returns
 */
export function parseDatetime(str: string): Date {
  // Set timezone to UTC to avoid issues with different timezones
  process.env.TZ = 'UTC'

  const timestamp = Date.parse(str)
  if (!isNaN(timestamp)) {
    return new Date(timestamp)
  } else {
    return null
  }
}
