import activitySpec from './activities'

describe('udm/activities', () => {
  it('should populate empty dates', () => {
    expect(
      activitySpec.populateFields({
        dueTime: null,
        startTime: '2020-01-01T00:00:00.000Z',
        endTime: null,
      }),
    ).toEqual({
      dueTime: '2020-01-01T00:00:00.000Z',
      startTime: '2020-01-01T00:00:00.000Z',
      endTime: '2020-01-01T00:00:00.000Z',
    })
  }),
    it('should not populate empty dates if there is nothing to be populated', () => {
      expect(
        activitySpec.populateFields({
          dueTime: null,
          startTime: null,
          endTime: null,
        }),
      ).toEqual({
        dueTime: null,
        startTime: null,
        endTime: null,
      })
    }),
    it('should not mess up w/ existing values', () => {
      expect(
        activitySpec.populateFields({
          dueTime: '2022-01-01T00:00:00.000Z',
          startTime: '2020-01-01T00:00:00.000Z',
          endTime: '2021-01-01T00:00:00.000Z',
        }),
      ).toEqual({
        dueTime: '2022-01-01T00:00:00.000Z',
        startTime: '2020-01-01T00:00:00.000Z',
        endTime: '2021-01-01T00:00:00.000Z',
      })
    })
})
