import { User } from '.'
import { ElementListAccessor, ElementAccessor } from '../accessors'
import { IntegrationAppClient } from '../client'
import { CreateUserRequest, FindUsersQuery, UpdateUserRequest } from './api'

export class UsersAccessor extends ElementListAccessor<
  User,
  FindUsersQuery,
  CreateUserRequest
> {
  constructor(client: IntegrationAppClient) {
    super(client, 'users')
  }
}

export class UserAccessor extends ElementAccessor<
  User,
  CreateUserRequest,
  UpdateUserRequest
> {
  constructor(client: IntegrationAppClient, idOrKey: string) {
    super({
      client,
      path: 'users',
      idOrKey,
    })
  }
}
